function user(id: string) {
    const parts = id.split("_");
    return parts[parts.length-1];
}

// manages all top-level DOM nodes inside a container
export class GraphView {
    container: HTMLElement;
    nodeMap: Map<HTMLElement, Node> = new Map();
    columns: Node[][] = [];
    padding: number = 24;
    arrowsSVG: SVGSVGElement;
    arrowMap: Map<HTMLElement, Arrow[]> = new Map();
    htmlFunction : Function;
    highlightFunction : Function;
    attentionNode: Node|null;
    scrolling: boolean = false;
    xScroll: number = 0;
    yScroll: number = 0;
    xScrollTarget: number = 0;
    yScrollTarget: number = 0;
    autoScroll: boolean = false;
    closedNodes: any[] = [];            // nodes we've closed but might reopen, stored as json
    canvasRect: Rect;                   // bounding-rect of all nodes, or window rect, whichever is bigger

    // setup: pass the div that's going to hold all nodes
    constructor(container: HTMLElement, htmlFunction: Function, highlightFunction: Function) {
        this.container = container;
        this.container.style.zIndex = `-10`;
        this.htmlFunction = htmlFunction;
        this.highlightFunction = highlightFunction;
        this.arrowsSVG = this.initArrows();
        this.attentionNode = null;
        this.canvasRect = new Rect(0, 0, document.body.clientWidth, document.body.clientHeight);
    }

    // given an id, returns the first div with that ID (expected to be unique)
    find(id: string, parent?:HTMLElement) : HTMLElement | null {
        if (!parent) parent = this.container;
        const elementsArray = Array.from(parent.querySelectorAll(`#${id}`));
        if (elementsArray.length == 0) return null;
        return elementsArray[0] as HTMLElement;
    }

    // given a div, returns the userObj of the first node that matches
    userObj(div: HTMLElement) : any {
        const node = this.get(div);
        if (!node) return null;
        return node.userObj;
    }

    // given a div, returns the linkDiv that opened it (todo: more than one? IDK)
    findLink(div: HTMLElement) : HTMLElement | null {
        const node = this.get(div);
        return node ? node.linkDiv : null;
    }

    // closes a div that's open, and all children as well
    close(div: HTMLElement) {
        const node = this.get(div)!;
        if (node) {
            let nodes = this.allChildren(node);
            for(let i = 1; i < nodes.length; i++) { 
                this.closedNodes.push(nodes[i].json());
            }
            //console.log("closed nodes:");
            //for(const c of this.closedNodes) { console.log("  ", user(c.id)) };
            for(let node of nodes) { this.disappear(node); }
        }
        this.arrangeAll();
    }

    // pay attention to (div)
    attention(div: HTMLElement) {
        this.attentionNode = this.get(div);
        //console.log("attention:", user(this.attentionNode!.div.id));
    }

    // animates to nothing over (time) seconds, then closes
    disappear(node: Node, time: number=0.25): void {
        let div = node.div;
        if (node.linkDiv) {
            this.removeArrow(node.linkDiv, div);
        } 
        div.style.width = div.scrollWidth + "px";
        div.style.height = div.scrollHeight + "px";
        div.style.transition = `width ${time}s, height ${time}s`;
        div.offsetWidth;
        div.offsetHeight;
        div.style.width = '0'; div.style.height = '0';
        setTimeout(() => {this.closeSingleNode(node);}, (time-0.1)*1000);
    }

    allChildren(node: Node) : Node[] {
        let result : Node[] = [ node ];
        for(let n of this.nodeMap.values()) {
            if (n.parentDiv == node.div) result.push(...this.allChildren(n));
        }
        return result;
    }

    closeSingleNode(node: Node) {
        this.nodeMap.delete(node.div);
        node.div.remove();
        node.remove();
    }

    reopen(id: string, linkID: string, parentID: string, userObj: any, emphasize: boolean=false) {
        let toOpen : any[] = [];

        let i = this.closedNodes.findIndex(n => n.id == id);        // is new node in closedNodes?
        if (i == -1) {
            toOpen = [ { id: id, link: linkID ?? "null",            // if no, make a new json version
                parent: parentID ?? "null",
                emphasize: emphasize,
                userObj: userObj }];
        } else {
            toOpen = this.closedNodes.splice(i, 1);                 // otherwise get it from closedNodes
        }

        // grab all nodes from closedNodes whose parents are in (tOpen)
        let iCheck = 0;
        while(iCheck < toOpen.length) {
            const p = toOpen[iCheck++];
            let remaining: any[] = [];
            for(let c of this.closedNodes) {
                if (c.parent == p.id) {
                    toOpen.push(c);
                } else {
                    remaining.push(c);
                }
            }
            this.closedNodes = remaining;
        }
        //console.log("reopen", user(id));
        for(const c of toOpen) {
            //console.log("   ", user(c.id));
        }

        // and open them
        this.openJsonNodeList(toOpen);
        this.attentionNode = this.get(this.find(id)!);
        //console.log("attention:", user(this.attentionNode!.div.id));
    }

    // opens a new node, generates the HTML
    open(id: string, linkID: string, parentID: string, userObj: any, emphasize: boolean=false) {
        if (this.find(id)) return;                      // don't make one if exists already
        let div = this.htmlFunction(id, userObj);
        div.style.transition = 'max-width 0.5s, max-height 0.5s, background-color 0.5s';
        let linkDiv = null;
        if (linkID != "" && parentID != "") {
            let parentDiv = this.find(parentID);
            if (parentDiv) linkDiv = this.find(linkID, parentDiv); else linkDiv = null;
        }
        if (linkDiv) {
            this.highlightFunction(linkDiv, true);
        }
        let node: Node = this.add(div, linkDiv, userObj);
        if (emphasize) {
            node!.emphasize = true;
        }
    }

    reset() { // close all except main-node
        for(let i = 1; i < this.columns.length; i++) {
            for(let node of this.columns[i]) {
                this.closeSingleNode(node);
            }
        }
        this.columns.splice(1, this.columns.length-1);
        this.closedNodes = [];
    }

    openJson(obj: any) {
        this.openJsonNodeList(obj.nodes);
        this.closedNodes = obj.closed;
        //console.log("closed nodelist:", this.closedNodes);
        this.xScroll = obj.xScroll;
        this.yScroll = obj.yScroll;
        scrollTo(this.xScroll, this.yScroll);
    }

    openJsonNodeList(nodesJson: any[]) {
        for(let n of nodesJson) {
            this.open(n.id, n.link, n.parent, n.userObj, n.emphasize);
        }
    }

    // adds a div to the manager, and to the container div
    add(div: HTMLElement, link: HTMLElement | null, userObj: any = null) : Node {
        this.container.appendChild(div);
        let parentDiv = (link)? this.findDivContainingLink(link) : null;
        let xTarget = 0;
        let yTarget = 0;
        if (link && parentDiv) {
            const prect = rect(parentDiv);
            const brect = rect(link);
            const crect = rect(div);
            xTarget = brect.left;
            yTarget = ((brect.top + brect.bottom)/2) - (crect.height()/2);
        } else {
            const crect = rect(div);
            xTarget = crect.left + 150;
            yTarget = (window.innerHeight / 2) - (crect.height()/2) - 32;
        }
        let node = new Node(this, div, link, parentDiv, userObj);
        node.setTargetPos(xTarget, yTarget);
        node.setPos(xTarget, yTarget);
        this.nodeMap.set(div, node);
        if (link && parentDiv) {
            this.addArrow(link, parentDiv, div);
        }
        this.arrangeAll();          // todo: call this every frame
        return node;
    }

    // emphasizes element (moves shadow forward in space)
    emphasize(div: HTMLElement, onOff: boolean) {
        let node = this.get(div);
        if (!node) return;
        node.emphasize = onOff; 
        node.updateShadow();
    }

    // arranges all views : computes xTarget, yTarget for each view
    arrangeAll() {
        this.sortColumns();
        let xPos = this.xMax(this.columns[0]);
        for(let i = 1; i < this.columns.length; i++) {
            let groups : Node[][] = this.splitColumnIntoGroups(i);
            for (let group of groups) {
                this.spaceNodesInGroup(group, xPos);
            }
            this.spaceGroupsVertically(groups);
            xPos = this.xMax(this.columns[i]);
        }
        const widthInPixels = getBodyWidth();
        const newWidth = Math.max(window.innerWidth, xPos);
        document.body.style.width = `${newWidth}px`;
    }

    // sort nodes within each column by link order
    sortColumns() {
        for(let col of this.columns) {
            col.sort((a, b) => ((a.linkIndex > b.linkIndex) ? 1 : ((a.linkIndex < b.linkIndex) ? -1 : 0)));
        }
    }

    // right edge of column
    xMax(nodes: Node[]) : number {
        let x = 0;
        for (const node of nodes) {
            x = Math.max(x, node.targetRect().right + (this.padding * 2));
        }
        return x;
    }

    // top edge of colum at xPos, or null if none intersect
    yRange(xPos: number) : [number | null, number | null] {
        for(let i=0; i < this.columns.length; i++) {
            let xMin = Infinity;
            let xMax = -1;
            let yBottom = -1;
            let yTop = Infinity;
            for(let node of this.columns[i]) {
                const r = rect(node.div);
                xMin = Math.min(r.left, xMin);
                xMax = Math.max(r.right + this.padding*2, xMax);
                yTop = Math.min(r.top, yTop);
                yBottom = Math.max(r.bottom, yBottom);
            }
            if (xMin <= xPos && xMax >= xPos) {
                return [yTop, yBottom];
            }
        }
        return [null, null];
    }

    // update
    update() {
        if (this.anyNodeSizeChanged()) {
            this.arrangeAll();
        }
        this.updateCanvas();
        this.updateScroll();
        for(let node of this.nodeMap.values()) {
            node.update();
        }
        this.updateArrows();
    }

    updateCanvas() {
        let bounds = new Rect(0,0, window.innerWidth, window.innerHeight);
        let padding = 10;

        // Calculate canvas rect based on node positions/sizes
        // note: bounds may be negative, that's fine
        for (const node of this.nodeMap.values()) {
            const r = node.targetRect();
            bounds.left = Math.min(bounds.left, r.left - padding);
            bounds.top = Math.min(bounds.top, r.top - padding);
            bounds.right = Math.max(bounds.right, r.right + padding);
            bounds.bottom = Math.max(bounds.bottom, r.bottom + padding);
        }
        bounds.left = Math.floor(bounds.left);
        bounds.top = Math.floor(bounds.top);
        bounds.right = Math.ceil(bounds.right);
        bounds.bottom = Math.ceil(bounds.bottom);
        if (bounds.left == this.canvasRect.left &&
            bounds.top == this.canvasRect.top &&
            bounds.right == this.canvasRect.right &&
            bounds.bottom == this.canvasRect.bottom) return;

        const deltaX = bounds.left - this.canvasRect.left;
        const deltaY = bounds.top - this.canvasRect.top;

        this.canvasRect = bounds;
        const xSize = this.canvasRect.width();
        const ySize = this.canvasRect.height();
        if (xSize != this.container.offsetWidth ||
            ySize != this.container.offsetHeight) {
            this.container.style.width = `${xSize}px`;
            this.container.style.height = `${ySize}px`;
            document.body.style.width = `${xSize}px`;
            document.body.style.height = `${ySize}px`;
            this.arrowsSVG.setAttribute('viewBox', `0 0 ${xSize} ${ySize}`);
            this.arrowsSVG.style.width = `${xSize}px`;
            this.arrowsSVG.style.height = `${ySize}px`;
        }

        // If any code-container div has drifted upwards or left, adjust all divs
        if (deltaX != 0 || deltaY != 0) {
            for (let child of Array.from(this.container.children)) {
                if (child instanceof HTMLElement) {
                    let div = child as HTMLElement;
                    div.style.left = `${div.offsetLeft - deltaX}px`;
                    div.style.top = `${div.offsetTop - deltaY}px`;
                }
            }
            window.scrollTo(window.scrollX - deltaX, window.scrollY - deltaY);
        }
    }

    updateScroll() {
        if (this.attentionNode && 
            (this.attentionNode.sizeChanged() ||
             this.attentionNode.moving())) {
                if (this.attentionNode.div.parentElement) {
                    [this.xScrollTarget, this.yScrollTarget] = scrollToView(this.attentionNode.div);
                } else {
                    this.attentionNode = null;
                }
        }
        if (window.scrollX == this.xScroll &&
            window.scrollY == this.yScroll) {
            if (this.scrolling && this.attentionNode) {
                this.attentionNode = null;
            }
            this.scrolling = false;
        } else {
            this.scrolling = true;
        }
        this.xScroll = window.scrollX;
        this.yScroll = window.scrollY;
    }

    // ------------------ private ----------------------------

    // returns the Node associated with a top-level div
    get(div: HTMLElement) : Node | null {
        let node = this.nodeMap.get(div);
        return node ? node : null;
    }

    // find the top-level div that contains a link elemeent
    findDivContainingLink(link: HTMLElement | null): HTMLElement | null {
        if (!link) return null;
        let parent = link.parentElement;
        while (parent && parent.parentElement !== this.container) {
            parent = parent.parentElement;
        }
        return (parent) ? parent : null;
    }

    anyNodeSizeChanged() : boolean {
        for(let node of this.nodeMap.values()) {
            if (node.sizeChanged())
                return true;
        }
        return false;
    }

    splitColumnIntoGroups(i: number) : Node[][] {
        let groups : Node[][] = [];
        for(const p of this.columns[i-1]) {
            let group : Node[] = [];
            for(const node of this.columns[i]) {
                if (node.parentDiv!.id === p.div.id) {
                    group.push(node);
                }
            }
            if (group.length > 0) {
                groups.push(group);
            }
        }
        return groups;
    }

    // within a group (nodes with same parent), space out around centerline
    spaceNodesInGroup(group: Node[], xPos: number) {
        // first find the total height of the group, plus padding
        let sumHeight = (group.length-1) * this.padding;
        for(const node of group) {
            sumHeight += node.targetRect().height();
        }

        // then find the centerline of the group's parent
        const parentDiv = group[0].parentDiv!;
        const parentNode = this.get(parentDiv)!;
        const parentRect = parentNode.targetRect();
        const centerLine = (parentRect.top + parentRect.bottom)/2;

        // now space group out vertically around the centerline
        let yPos = Math.max(this.padding, centerLine - (sumHeight/2));
        let pivot = (group.length-1)/2;
        for (let i = 0; i < group.length; i++) {
            let node = group[i];
            let xOffset = (i < pivot) ? i : (pivot - (i - pivot));
            node.setTargetPos(xPos + (xOffset * this.padding), yPos);
            yPos += node.targetRect().height() + this.padding;
        }
    }

    // space groups out vertically so they don't overlap
    spaceGroupsVertically(groups: Node[][]) {
        if (groups.length < 2) return;
        const even = (groups.length % 2) == 0;
        const iPivot = Math.floor((groups.length-1)/2);
        if (even) {
            this.checkGroups(groups[iPivot], groups[iPivot+1], 0.5);
        }
        for(let i=iPivot; i>0; i--) {
            this.checkGroups(groups[i-1], groups[1], 0);
        }
        for(let i=iPivot; i < groups.length-1; i++) {
            this.checkGroups(groups[i], groups[i+1], 1);
        }
    }

    checkGroups(groupA: Node[], groupB: Node[], mix: number) {
        const bottomA = groupA[groupA.length-1].targetRect().bottom;
        const topB = groupB[0].targetRect().top;
        const overlap =  (bottomA + (this.padding*2)) - topB;
        if (overlap < 0) return;
        const moveA = -(overlap * (1 - mix));
        const moveB = overlap * mix;
        this.moveGroupVertically(groupA, moveA);
        this.moveGroupVertically(groupB, moveB);
    }

    moveGroupVertically(group: Node[], yMove: number) {
        for(let node of group) {
            node.yTarget += yMove;
        }
    }

    //----------------------------- internal --------------------------------

    // add a Node to the correct column, keep them sorted vertically
    addToColumnArray(node : Node) {
        while (this.columns.length <= node.column) {
            this.columns.push([]);
        }
        this.columns[node.column].push(node);
    }

    //----------------------------- arrows ---------------------------------

    // set up the SVG arrow renderer
    initArrows() : SVGSVGElement {
        const svg = document.createElementNS('http://www.w3.org/2000/svg', 'svg');
        svg.style.position = 'absolute';
        svg.style.width = '100%';
        svg.style.height = '100%';
        svg.style.pointerEvents = 'none';
        svg.style.zIndex = '1000';
        this.container.appendChild(svg);
        return svg;
    }

    addArrow(linkDiv: HTMLElement, parentDiv: HTMLElement, div: HTMLElement) {
        let arrow = new Arrow(linkDiv, parentDiv, div);
        if (!this.arrowMap.has(linkDiv)) { this.arrowMap.set(linkDiv, []); }
        this.arrowMap.get(linkDiv)!.push(arrow);
        arrow.addToSVG(this.arrowsSVG);
    }

    removeArrow(linkDiv: HTMLElement, div: HTMLElement) {
        let arrows : Arrow[] | undefined = this.arrowMap.get(linkDiv);
        if (arrows) {
            const index = arrows.findIndex(a => a.div === div);
            if (index >= 0) {
                let arrow: Arrow = arrows[index];
                arrows.splice(index, 1);
                arrow.removeFromSVG();
            }
        }
    }

    updateArrows() {
        for (let arrows of this.arrowMap.values()) {
            for (let arrow of arrows) {
                arrow.update();
            }
        }
    }

    json() : any {
        let node : Node = this.columns[0][0];
        let nodes: Node[] = this.allChildren(node);
        return { xScroll: this.xScroll, yScroll: this.yScroll,
                 nodes:  nodes.map(node => node.json()),
                 closed: this.closedNodes };
    }
}

// stores information about a div we're managing
class Node {
    graph: GraphView;                       // the view we're a part of
    userObj: any;                           // per-node user info
    div: HTMLElement;                       // the div we're tracking
    linkDiv: HTMLElement | null;            // the link that triggered this
    parentDiv: HTMLElement | null;          // the parent div of the link div that opened this node
    parentNode: Node | null = null;         // the parent node corresponding to parentDiv
    column: number = 0;                     // column we're in (first one zero)
    x: number = 0;                          // position right now
    y: number = 0;                          // ..
    xTarget: number = 0;                    // where we're trying to get to, to avoid others
    yTarget: number = 0;                    // ..
    width: number =0;                       // width on last frame, so we can react to animation
    height: number =0;                      // ..
    shadow: HTMLElement;                    // the shadow, all-important :-)
    emphasize: boolean = false;             // if set, comes forward in the stack
    linkIndex: string = "";                 // sort index for vertical ordering

    constructor(view: GraphView, div: HTMLElement, linkDiv: HTMLElement | null, parentDiv: HTMLElement | null, userObj: any =null) {
        this.graph = view;
        this.userObj = userObj;
        if (parentDiv === undefined) {
            console.log("WARNING: parentDiv undefined!");
        }
        this.div = div; this.linkDiv = linkDiv; this.parentDiv = parentDiv;
        if (parentDiv) { 
            this.column = this.graph.get(parentDiv)!.column + 1;
            this.parentNode = this.graph.get(parentDiv);
            this.linkIndex = this.computeLinkIndex();
        }
        this.graph.addToColumnArray(this);

        this.shadow = element(`<div class="shadow"></div>`);
        this.graph.container.appendChild(this.shadow);
        this.width = div.clientWidth; this.height = div.clientHeight;
    }

    remove() {
        this.graph.columns[this.column] = this.graph.columns[this.column].filter(item => item !== this);
        this.shadow.remove();
    }

    setTargetPos(x: number, y: number) {
        this.xTarget = x;
        this.yTarget = y;
    }

    setPos(x: number, y: number) {
        this.x = x; this.y = y;
        this.div.style.left = `${x - this.graph.canvasRect.left}px`;
        this.div.style.top = `${y - this.graph.canvasRect.top}px`;
    }

    targetRect() : Rect {
        const r = rect(this.div);
        return new Rect(this.xTarget, this.yTarget, this.xTarget + r.width(), this.yTarget + r.height());
    }

    sizeChanged() : boolean {
        return (this.width != this.div.clientWidth ||
                this.height != this.div.clientHeight);
    }

    moving() : boolean {
        return (this.x != this.xTarget || this.y != this.yTarget);
    }

    update() {
        const dx = this.xTarget - this.x;
        const dy = this.yTarget - this.y;
        const x = (Math.abs(dx)<=1) ? this.xTarget : (this.x + dx*0.1);
        const y = (Math.abs(dy)<=1) ? this.yTarget : (this.y + dy*0.1);
        this.setPos(x, y);
        this.updateShadow();
        this.width = this.div.clientWidth; this.height = this.div.clientHeight;
    }

    computeLinkIndex() : string {
        let result = "";
        let digit = 1;
        let node : Node | null = this;
        while(node) {
            if (node.linkDiv) {
                let i = getChildNodeIndex(node.linkDiv).toString().padStart(3, '0');
                result = (result=="") ? i : i + '.' + result;
            }
            node = node.parentNode;
        }
        return result;
    }

    updateShadow() {
        const sr = rect(this.div);
        const wh = window.innerHeight;
        let sy = (this.emphasize) ? (sr.bottom + 150) : (sr.bottom + 50);
        sy = Math.max(wh/2+20, sy);
        this.shadow.style.left = `${sr.left}px`;
        this.shadow.style.top = `${sy}px`;
        this.shadow.style.width = `${sr.width()}px`;
        this.shadow.style.height = `1px`;
        this.shadow.style.zIndex = `-1`;
    }

    json() : any {
        return { id: this.div.id,
                 link: this.linkDiv?.id ?? "null",
                 parent: this.parentDiv?.id ?? "null",
                 emphasize: this.emphasize,
                 userObj: this.userObj };
    }
}

class Arrow {
    linkDiv: HTMLElement;
    parentDiv: HTMLElement;
    div: HTMLElement;
    drawRect: Rect = new Rect(0,0,0,0);
    xVertical: number = 0;
    path: SVGPathElement;
    constructor(linkDiv: HTMLElement, parentDiv: HTMLElement, div: HTMLElement) {
        this.linkDiv = linkDiv; this.parentDiv = parentDiv; this.div = div;
        this.path = document.createElementNS('http://www.w3.org/2000/svg', 'path');
        this.path.setAttribute('stroke', '#87bdb5');
        this.path.setAttribute('stroke-width', '3');
        this.path.setAttribute('stroke-opacity', '1');
        this.path.setAttribute('fill', 'transparent');
    }
    addToSVG(svg: SVGSVGElement) {
        svg.appendChild(this.path);
    }
    removeFromSVG() {
        this.path.remove();
    }
    update() {
        this.initDrawRect();
        this.updatePath();
    }
    initDrawRect() {
        const parentRect = rect(this.parentDiv);
        const linkRect = rect(this.linkDiv);
        const targetRect = rect(this.div);
        const xFrom = parentRect.right + 2;
        let yFrom = (linkRect.top + linkRect.bottom)/2;
        yFrom = Math.max(yFrom, parentRect.top + 12);
        yFrom = Math.min(yFrom, parentRect.bottom - 12);
        const xTo = targetRect.left - 2;
        let yTo = yFrom;
        yTo = Math.max(yTo, targetRect.top + 12);
        yTo = Math.min(yTo, targetRect.bottom - 12);
        this.drawRect = new Rect(xFrom, yFrom, xTo, yTo);
        this.xVertical = (xFrom + xTo)/2;
    }
    updatePath() {
        /*if (!document.body.contains(this.linkDiv) || !document.body.contains(this.div)) {
            this.removeFromSVG();
            return;
        }*/
        const startX = this.drawRect.left;
        const startY = this.drawRect.top;
        const endX = this.drawRect.right;
        const endY = this.drawRect.bottom;
        const cornerRadius = Math.min(Math.abs(endY-startY)/2, 10);
        const midX = this.xVertical;
        const vs = (endY < startY) ? -1 : +1;

        const d = [
            `M ${startX} ${startY}`,
            `H ${midX - cornerRadius}`,
            `Q ${midX} ${startY} ${midX} ${startY + vs*cornerRadius}`,
            `V ${endY - vs*cornerRadius}`,
            `Q ${midX} ${endY} ${midX + cornerRadius} ${endY}`,
            `H ${endX}`,
        ].join(' ');

        this.path.setAttribute('d', d);
    }
}

// setup: pass the div that's going to hold all nodes
constructor(container: HTMLElement, htmlFunction: Function, highlightFunction: Function) {
    this.container = container;
    this.container.style.zIndex = `-10`;
    this.htmlFunction = htmlFunction;
    this.highlightFunction = highlightFunction;
    this.arrowsSVG = this.initArrows();
    this.attentionNode = null;
    this.canvasRect = new Rect(0, 0, document.body.clientWidth, document.body.clientHeight);
}

// given an id, returns the first div with that ID (expected to be unique)
find(id: string, parent?:HTMLElement) : HTMLElement | null {
    if (!parent) parent = this.container;
    const elementsArray = Array.from(parent.querySelectorAll(`#${id}`));
    if (elementsArray.length == 0) return null;
    return elementsArray[0] as HTMLElement;
}

// given a div, returns the userObj of the first node that matches
userObj(div: HTMLElement) : any {
    const node = this.get(div);
    if (!node) return null;
    return node.userObj;
}

// given a div, returns the linkDiv that opened it (todo: more than one? IDK)
findLink(div: HTMLElement) : HTMLElement | null {
    const node = this.get(div);
    return node ? node.linkDiv : null;
}

// closes a div that's open, and all children as well
close(div: HTMLElement) {
    const node = this.get(div)!;
    if (node) {
        let nodes = this.allChildren(node);
        for(let i = 1; i < nodes.length; i++) { 
            this.closedNodes.push(nodes[i].json());
        }
        //console.log("closed nodes:");
        //for(const c of this.closedNodes) { console.log("  ", user(c.id)) };
        for(let node of nodes) { this.disappear(node); }
    }
    this.arrangeAll();
}

// pay attention to (div)
attention(div: HTMLElement) {
    this.attentionNode = this.get(div);
    //console.log("attention:", user(this.attentionNode!.div.id));
}

// animates to nothing over (time) seconds, then closes
disappear(node: Node, time: number=0.25): void {
    let div = node.div;
    if (node.linkDiv) {
        this.removeArrow(node.linkDiv, div);
    } 
    div.style.width = div.scrollWidth + "px";
    div.style.height = div.scrollHeight + "px";
    div.style.transition = `width ${time}s, height ${time}s`;
    div.offsetWidth;
    div.offsetHeight;
    div.style.width = '0'; div.style.height = '0';
    setTimeout(() => {this.closeSingleNode(node);}, (time-0.1)*1000);
}

allChildren(node: Node) : Node[] {
    let result : Node[] = [ node ];
    for(let n of this.nodeMap.values()) {
        if (n.parentDiv == node.div) result.push(...this.allChildren(n));
    }
    return result;
}

closeSingleNode(node: Node) {
    this.nodeMap.delete(node.div);
    node.div.remove();
    node.remove();
}

reopen(id: string, linkID: string, parentID: string, userObj: any, emphasize: boolean=false) {
    let toOpen : any[] = [];

    let i = this.closedNodes.findIndex(n => n.id == id);        // is new node in closedNodes?
    if (i == -1) {
        toOpen = [ { id: id, link: linkID ?? "null",            // if no, make a new json version
            parent: parentID ?? "null",
            emphasize: emphasize,
            userObj: userObj }];
    } else {
        toOpen = this.closedNodes.splice(i, 1);                 // otherwise get it from closedNodes
    }

    // grab all nodes from closedNodes whose parents are in (tOpen)
    let iCheck = 0;
    while(iCheck < toOpen.length) {
        const p = toOpen[iCheck++];
        let remaining: any[] = [];
        for(let c of this.closedNodes) {
            if (c.parent == p.id) {
                toOpen.push(c);
            } else {
                remaining.push(c);
            }
        }
        this.closedNodes = remaining;
    }
    //console.log("reopen", user(id));
    for(const c of toOpen) {
        //console.log("   ", user(c.id));
    }

    // and open them
    this.openJsonNodeList(toOpen);
    this.attentionNode = this.get(this.find(id)!);
    //console.log("attention:", user(this.attentionNode!.div.id));
}

// opens a new node, generates the HTML
open(id: string, linkID: string, parentID: string, userObj: any, emphasize: boolean=false) {
    if (this.find(id)) return;                      // don't make one if exists already
    let div = this.htmlFunction(id, userObj);
    div.style.transition = 'max-width 0.5s, max-height 0.5s, background-color 0.5s';
    let linkDiv = null;
    if (linkID != "" && parentID != "") {
        let parentDiv = this.find(parentID);
        if (parentDiv) linkDiv = this.find(linkID, parentDiv); else linkDiv = null;
    }
    if (linkDiv) {
        this.highlightFunction(linkDiv, true);
    }
    let node: Node = this.add(div, linkDiv, userObj);
    if (emphasize) {
        node!.emphasize = true;
    }
}

reset() { // close all except main-node
    for(let i = 1; i < this.columns.length; i++) {
        for(let node of this.columns[i]) {
            this.closeSingleNode(node);
        }
    }
    this.columns.splice(1, this.columns.length-1);
    this.closedNodes = [];
}

openJson(obj: any) {
    this.openJsonNodeList(obj.nodes);
    this.closedNodes = obj.closed;
    //console.log("closed nodelist:", this.closedNodes);
    this.xScroll = obj.xScroll;
    this.yScroll = obj.yScroll;
    scrollTo(this.xScroll, this.yScroll);
}

openJsonNodeList(nodesJson: any[]) {
    for(let n of nodesJson) {
        this.open(n.id, n.link, n.parent, n.userObj, n.emphasize);
    }
}

// adds a div to the manager, and to the container div
add(div: HTMLElement, link: HTMLElement | null, userObj: any = null) : Node {
    this.container.appendChild(div);
    let parentDiv = (link)? this.findDivContainingLink(link) : null;
    let xTarget = 0;
    let yTarget = 0;
    if (link && parentDiv) {
        const prect = rect(parentDiv);
        const brect = rect(link);
        const crect = rect(div);
        xTarget = brect.left;
        yTarget = ((brect.top + brect.bottom)/2) - (crect.height()/2);
    } else {
        const crect = rect(div);
        xTarget = crect.left + 150;
        yTarget = (window.innerHeight / 2) - (crect.height()/2) - 32;
    }
    let node = new Node(this, div, link, parentDiv, userObj);
    node.setTargetPos(xTarget, yTarget);
    node.setPos(xTarget, yTarget);
    this.nodeMap.set(div, node);
    if (link && parentDiv) {
        this.addArrow(link, parentDiv, div);
    }
    this.arrangeAll();          // todo: call this every frame
    return node;
}

// emphasizes element (moves shadow forward in space)
emphasize(div: HTMLElement, onOff: boolean) {
    let node = this.get(div);
    if (!node) return;
    node.emphasize = onOff; 
    node.updateShadow();
}

// arranges all views : computes xTarget, yTarget for each view
arrangeAll() {
    this.sortColumns();
    let xPos = this.xMax(this.columns[0]);
    for(let i = 1; i < this.columns.length; i++) {
        let groups : Node[][] = this.splitColumnIntoGroups(i);
        for (let group of groups) {
            this.spaceNodesInGroup(group, xPos);
        }
        this.spaceGroupsVertically(groups);
        xPos = this.xMax(this.columns[i]);
    }
    const widthInPixels = getBodyWidth();
    const newWidth = Math.max(window.innerWidth, xPos);
    document.body.style.width = `${newWidth}px`;
}

// sort nodes within each column by link order
sortColumns() {
    for(let col of this.columns) {
        col.sort((a, b) => ((a.linkIndex > b.linkIndex) ? 1 : ((a.linkIndex < b.linkIndex) ? -1 : 0)));
    }
}

// right edge of column
xMax(nodes: Node[]) : number {
    let x = 0;
    for (const node of nodes) {
        x = Math.max(x, node.targetRect().right + (this.padding * 2));
    }
    return x;
}

// top edge of colum at xPos, or null if none intersect
yRange(xPos: number) : [number | null, number | null] {
    for(let i=0; i < this.columns.length; i++) {
        let xMin = Infinity;
        let xMax = -1;
        let yBottom = -1;
        let yTop = Infinity;
        for(let node of this.columns[i]) {
            const r = rect(node.div);
            xMin = Math.min(r.left, xMin);
            xMax = Math.max(r.right + this.padding*2, xMax);
            yTop = Math.min(r.top, yTop);
            yBottom = Math.max(r.bottom, yBottom);
        }
        if (xMin <= xPos && xMax >= xPos) {
            return [yTop, yBottom];
        }
    }
    return [null, null];
}

// update
update() {
    if (this.anyNodeSizeChanged()) {
        this.arrangeAll();
    }
    this.updateCanvas();
    this.updateScroll();
    for(let node of this.nodeMap.values()) {
        node.update();
    }
    this.updateArrows();
}

updateCanvas() {
    let bounds = new Rect(0,0, window.innerWidth, window.innerHeight);
    let padding = 10;

    // Calculate canvas rect based on node positions/sizes
    // note: bounds may be negative, that's fine
    for (const node of this.nodeMap.values()) {
        const r = node.targetRect();
        bounds.left = Math.min(bounds.left, r.left - padding);
        bounds.top = Math.min(bounds.top, r.top - padding);
        bounds.right = Math.max(bounds.right, r.right + padding);
        bounds.bottom = Math.max(bounds.bottom, r.bottom + padding);
    }
    bounds.left = Math.floor(bounds.left);
    bounds.top = Math.floor(bounds.top);
    bounds.right = Math.ceil(bounds.right);
    bounds.bottom = Math.ceil(bounds.bottom);
    if (bounds.left == this.canvasRect.left &&
        bounds.top == this.canvasRect.top &&
        bounds.right == this.canvasRect.right &&
        bounds.bottom == this.canvasRect.bottom) return;

    const deltaX = bounds.left - this.canvasRect.left;
    const deltaY = bounds.top - this.canvasRect.top;

    this.canvasRect = bounds;
    const xSize = this.canvasRect.width();
    const ySize = this.canvasRect.height();
    if (xSize != this.container.offsetWidth ||
        ySize != this.container.offsetHeight) {
        this.container.style.width = `${xSize}px`;
        this.container.style.height = `${ySize}px`;
        document.body.style.width = `${xSize}px`;
        document.body.style.height = `${ySize}px`;
        this.arrowsSVG.setAttribute('viewBox', `0 0 ${xSize} ${ySize}`);
        this.arrowsSVG.style.width = `${xSize}px`;
        this.arrowsSVG.style.height = `${ySize}px`;
    }

    // If any code-container div has drifted upwards or left, adjust all divs
    if (deltaX != 0 || deltaY != 0) {
        for (let child of Array.from(this.container.children)) {
            if (child instanceof HTMLElement) {
                let div = child as HTMLElement;
                div.style.left = `${div.offsetLeft - deltaX}px`;
                div.style.top = `${div.offsetTop - deltaY}px`;
            }
        }
        window.scrollTo(window.scrollX - deltaX, window.scrollY - deltaY);
    }
}

updateScroll() {
    if (this.attentionNode && 
        (this.attentionNode.sizeChanged() ||
         this.attentionNode.moving())) {
            if (this.attentionNode.div.parentElement) {
                [this.xScrollTarget, this.yScrollTarget] = scrollToView(this.attentionNode.div);
            } else {
                this.attentionNode = null;
            }
    }
    if (window.scrollX == this.xScroll &&
        window.scrollY == this.yScroll) {
        if (this.scrolling && this.attentionNode) {
            this.attentionNode = null;
        }
        this.scrolling = false;
    } else {
        this.scrolling = true;
    }
    this.xScroll = window.scrollX;
    this.yScroll = window.scrollY;
}

// returns the Node associated with a top-level div
get(div: HTMLElement) : Node | null {
    let node = this.nodeMap.get(div);
    return node ? node : null;
}

// find the top-level div that contains a link elemeent
findDivContainingLink(link: HTMLElement | null): HTMLElement | null {
    if (!link) return null;
    let parent = link.parentElement;
    while (parent && parent.parentElement !== this.container) {
        parent = parent.parentElement;
    }
    return (parent) ? parent : null;
}

anyNodeSizeChanged() : boolean {
    for(let node of this.nodeMap.values()) {
        if (node.sizeChanged())
            return true;
    }
    return false;
}

splitColumnIntoGroups(i: number) : Node[][] {
    let groups : Node[][] = [];
    for(const p of this.columns[i-1]) {
        let group : Node[] = [];
        for(const node of this.columns[i]) {
            if (node.parentDiv!.id === p.div.id) {
                group.push(node);
            }
        }
        if (group.length > 0) {
            groups.push(group);
        }
    }
    return groups;
}

// within a group (nodes with same parent), space out around centerline
spaceNodesInGroup(group: Node[], xPos: number) {
    // first find the total height of the group, plus padding
    let sumHeight = (group.length-1) * this.padding;
    for(const node of group) {
        sumHeight += node.targetRect().height();
    }

    // then find the centerline of the group's parent
    const parentDiv = group[0].parentDiv!;
    const parentNode = this.get(parentDiv)!;
    const parentRect = parentNode.targetRect();
    const centerLine = (parentRect.top + parentRect.bottom)/2;

    // now space group out vertically around the centerline
    let yPos = Math.max(this.padding, centerLine - (sumHeight/2));
    let pivot = (group.length-1)/2;
    for (let i = 0; i < group.length; i++) {
        let node = group[i];
        let xOffset = (i < pivot) ? i : (pivot - (i - pivot));
        node.setTargetPos(xPos + (xOffset * this.padding), yPos);
        yPos += node.targetRect().height() + this.padding;
    }
}

// space groups out vertically so they don't overlap
spaceGroupsVertically(groups: Node[][]) {
    if (groups.length < 2) return;
    const even = (groups.length % 2) == 0;
    const iPivot = Math.floor((groups.length-1)/2);
    if (even) {
        this.checkGroups(groups[iPivot], groups[iPivot+1], 0.5);
    }
    for(let i=iPivot; i>0; i--) {
        this.checkGroups(groups[i-1], groups[1], 0);
    }
    for(let i=iPivot; i < groups.length-1; i++) {
        this.checkGroups(groups[i], groups[i+1], 1);
    }
}

checkGroups(groupA: Node[], groupB: Node[], mix: number) {
    const bottomA = groupA[groupA.length-1].targetRect().bottom;
    const topB = groupB[0].targetRect().top;
    const overlap =  (bottomA + (this.padding*2)) - topB;
    if (overlap < 0) return;
    const moveA = -(overlap * (1 - mix));
    const moveB = overlap * mix;
    this.moveGroupVertically(groupA, moveA);
    this.moveGroupVertically(groupB, moveB);
}

moveGroupVertically(group: Node[], yMove: number) {
    for(let node of group) {
        node.yTarget += yMove;
    }
}

// add a Node to the correct column, keep them sorted vertically
addToColumnArray(node : Node) {
    while (this.columns.length <= node.column) {
        this.columns.push([]);
    }
    this.columns[node.column].push(node);
}

// set up the SVG arrow renderer
initArrows() : SVGSVGElement {
    const svg = document.createElementNS('http://www.w3.org/2000/svg', 'svg');
    svg.style.position = 'absolute';
    svg.style.width = '100%';
    svg.style.height = '100%';
    svg.style.pointerEvents = 'none';
    svg.style.zIndex = '1000';
    this.container.appendChild(svg);
    return svg;
}

addArrow(linkDiv: HTMLElement, parentDiv: HTMLElement, div: HTMLElement) {
    let arrow = new Arrow(linkDiv, parentDiv, div);
    if (!this.arrowMap.has(linkDiv)) { this.arrowMap.set(linkDiv, []); }
    this.arrowMap.get(linkDiv)!.push(arrow);
    arrow.addToSVG(this.arrowsSVG);
}

removeArrow(linkDiv: HTMLElement, div: HTMLElement) {
    let arrows : Arrow[] | undefined = this.arrowMap.get(linkDiv);
    if (arrows) {
        const index = arrows.findIndex(a => a.div === div);
        if (index >= 0) {
            let arrow: Arrow = arrows[index];
            arrows.splice(index, 1);
            arrow.removeFromSVG();
        }
    }
}

updateArrows() {
    for (let arrows of this.arrowMap.values()) {
        for (let arrow of arrows) {
            arrow.update();
        }
    }
}

    json() : any {
        let node : Node = this.columns[0][0];
        let nodes: Node[] = this.allChildren(node);
        return { xScroll: this.xScroll, yScroll: this.yScroll,
                 nodes:  nodes.map(node => node.json()),
                 closed: this.closedNodes };
    }
}

constructor(view: GraphView, div: HTMLElement, linkDiv: HTMLElement | null, parentDiv: HTMLElement | null, userObj: any =null) {
    this.graph = view;
    this.userObj = userObj;
    if (parentDiv === undefined) {
        console.log("WARNING: parentDiv undefined!");
    }
    this.div = div; this.linkDiv = linkDiv; this.parentDiv = parentDiv;
    if (parentDiv) { 
        this.column = this.graph.get(parentDiv)!.column + 1;
        this.parentNode = this.graph.get(parentDiv);
        this.linkIndex = this.computeLinkIndex();
    }
    this.graph.addToColumnArray(this);

    this.shadow = element(`<div class="shadow"></div>`);
    this.graph.container.appendChild(this.shadow);
    this.width = div.clientWidth; this.height = div.clientHeight;
}

remove() {
    this.graph.columns[this.column] = this.graph.columns[this.column].filter(item => item !== this);
    this.shadow.remove();
}

setTargetPos(x: number, y: number) {
    this.xTarget = x;
    this.yTarget = y;
}

setPos(x: number, y: number) {
    this.x = x; this.y = y;
    this.div.style.left = `${x - this.graph.canvasRect.left}px`;
    this.div.style.top = `${y - this.graph.canvasRect.top}px`;
}

targetRect() : Rect {
    const r = rect(this.div);
    return new Rect(this.xTarget, this.yTarget, this.xTarget + r.width(), this.yTarget + r.height());
}

sizeChanged() : boolean {
    return (this.width != this.div.clientWidth ||
            this.height != this.div.clientHeight);
}

moving() : boolean {
    return (this.x != this.xTarget || this.y != this.yTarget);
}

update() {
    const dx = this.xTarget - this.x;
    const dy = this.yTarget - this.y;
    const x = (Math.abs(dx)<=1) ? this.xTarget : (this.x + dx*0.1);
    const y = (Math.abs(dy)<=1) ? this.yTarget : (this.y + dy*0.1);
    this.setPos(x, y);
    this.updateShadow();
    this.width = this.div.clientWidth; this.height = this.div.clientHeight;
}

computeLinkIndex() : string {
    let result = "";
    let digit = 1;
    let node : Node | null = this;
    while(node) {
        if (node.linkDiv) {
            let i = getChildNodeIndex(node.linkDiv).toString().padStart(3, '0');
            result = (result=="") ? i : i + '.' + result;
        }
        node = node.parentNode;
    }
    return result;
}

updateShadow() {
    const sr = rect(this.div);
    const wh = window.innerHeight;
    let sy = (this.emphasize) ? (sr.bottom + 150) : (sr.bottom + 50);
    sy = Math.max(wh/2+20, sy);
    this.shadow.style.left = `${sr.left}px`;
    this.shadow.style.top = `${sy}px`;
    this.shadow.style.width = `${sr.width()}px`;
    this.shadow.style.height = `1px`;
    this.shadow.style.zIndex = `-1`;
}

    json() : any {
        return { id: this.div.id,
                 link: this.linkDiv?.id ?? "null",
                 parent: this.parentDiv?.id ?? "null",
                 emphasize: this.emphasize,
                 userObj: this.userObj };
    }
}

constructor(linkDiv: HTMLElement, parentDiv: HTMLElement, div: HTMLElement) {
    this.linkDiv = linkDiv; this.parentDiv = parentDiv; this.div = div;
    this.path = document.createElementNS('http://www.w3.org/2000/svg', 'path');
    this.path.setAttribute('stroke', '#87bdb5');
    this.path.setAttribute('stroke-width', '3');
    this.path.setAttribute('stroke-opacity', '1');
    this.path.setAttribute('fill', 'transparent');
}

addToSVG(svg: SVGSVGElement) {
    svg.appendChild(this.path);
}

removeFromSVG() {
    this.path.remove();
}

update() {
    this.initDrawRect();
    this.updatePath();
}

initDrawRect() {
    const parentRect = rect(this.parentDiv);
    const linkRect = rect(this.linkDiv);
    const targetRect = rect(this.div);
    const xFrom = parentRect.right + 2;
    let yFrom = (linkRect.top + linkRect.bottom)/2;
    yFrom = Math.max(yFrom, parentRect.top + 12);
    yFrom = Math.min(yFrom, parentRect.bottom - 12);
    const xTo = targetRect.left - 2;
    let yTo = yFrom;
    yTo = Math.max(yTo, targetRect.top + 12);
    yTo = Math.min(yTo, targetRect.bottom - 12);
    this.drawRect = new Rect(xFrom, yFrom, xTo, yTo);
    this.xVertical = (xFrom + xTo)/2;
}

    updatePath() {
        /*if (!document.body.contains(this.linkDiv) || !document.body.contains(this.div)) {
            this.removeFromSVG();
            return;
        }*/
        const startX = this.drawRect.left;
        const startY = this.drawRect.top;
        const endX = this.drawRect.right;
        const endY = this.drawRect.bottom;
        const cornerRadius = Math.min(Math.abs(endY-startY)/2, 10);
        const midX = this.xVertical;
        const vs = (endY < startY) ? -1 : +1;

        const d = [
            `M ${startX} ${startY}`,
            `H ${midX - cornerRadius}`,
            `Q ${midX} ${startY} ${midX} ${startY + vs*cornerRadius}`,
            `V ${endY - vs*cornerRadius}`,
            `Q ${midX} ${endY} ${midX + cornerRadius} ${endY}`,
            `H ${endX}`,
        ].join(' ');

        this.path.setAttribute('d', d);
    }
}

export class Rect {
    left: number;
    top: number;
    right: number;
    bottom: number;
    constructor(left: number, top: number, right: number, bottom: number) {
        this.left = left; this.top = top; this.right = right; this.bottom = bottom; 
    }
    width() : number { return this.right - this.left; }
    height() : number { return this.bottom - this.top; }
}

export function element(html: string) : HTMLElement {
    let div = document.createElement('div');
    div.innerHTML = html;  
    return div.firstChild as HTMLElement;
}

// given an element, returns the rectangle relative to document origin
export function rect(el: HTMLElement) : Rect {
            const r = el.getBoundingClientRect();
        return new Rect(r.left + window.scrollX, r.top + window.scrollY,
                    r.right + window.scrollX, r.bottom + window.scrollY);
}

// find width of body element
export function getBodyWidth() : number {
    let bodyWidth: string = getComputedStyle(document.body).width;
    return parseInt(bodyWidth, 10);
}

export function scrollToView(div: HTMLElement, padding: number = 48) : [number, number]{
    const rect = div.getBoundingClientRect();
    const windowHeight = window.innerHeight;
    const windowWidth = window.innerWidth;
    let x = window.scrollX;
    let y = window.scrollY;
    // Check vertical visibility
    if (rect.top - padding < 0) {
        // The div is above the viewport
        y= window.scrollY + rect.top - padding;
    } else if (rect.bottom + padding > windowHeight) {
        // The div is below the viewport
        y= window.scrollY + rect.bottom + padding - windowHeight;
    }
    // Check horizontal visibility
    if (rect.left - padding < 0) {
        // The div is to the left of the viewport
        x = window.scrollX + rect.left - padding;
    } else if (rect.right + padding > windowWidth) {
        // The div is to the right of the viewport
        x = window.scrollX + rect.right + padding - windowWidth;
    }
    // Perform the scroll if needed
    if (x != window.scrollX || y != window.scrollY) {
        window.scrollTo(x, y);
    }
    return [x, y];
}

export function scrollTo(x: number | undefined, y: number | undefined) {
    let scrollOptions: ScrollToOptions = {
        behavior: 'smooth'
    };
    scrollOptions.left = x;
    scrollOptions.top = y;
    window.scrollTo(scrollOptions);
}

export function splitArray<T>(arr: T[], cond: (item: T) => boolean): [T[], T[]] {
    const trueArray: T[] = [];
    const falseArray: T[] = [];

    arr.forEach(item => {
        if (cond(item)) {
            trueArray.push(item);
        } else {
            falseArray.push(item);
        }
    });

    return [trueArray, falseArray];
}

// Debounce function
export function debounce<T extends (...args: any[]) => void>(func: T, wait: number): (...args: Parameters<T>) => void {
    let timeout: number | undefined;
    return function (this: ThisParameterType<T>, ...args: Parameters<T>) {
        clearTimeout(timeout);

        timeout = setTimeout(() => {
            func.apply(this, args);
        }, wait) as unknown as number;
    };
}

export function getChildNodeIndex(element: Element): number {
    return Array.from(element.parentElement!.children).indexOf(element);
}

// sends a command request to the server, waits on the reply, returns dictionary object
export async function remote(endpointAndFunc: string, args: any) : Promise<any> {
    if (!endpointAndFunc.startsWith("@")) {
        console.error(`endpoint '${endpointAndFunc}' must start with '@'`);
    }
    const parts = endpointAndFunc.split(".");
    const endpoint = parts[0].slice(1);
    const func = parts[1];
    try {
        const response = await fetch(`http://localhost:8000/${endpoint}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ func: func, args: args })
        });
        if (!response.ok) {
            throw new Error('Network response was not ok');
        }
        const obj= await response.json();
        return obj;
    } catch (error) {
        console.error('Error:', error);
    }
    return [];
}

